sap.ui.define(["sap/ui/core/mvc/Controller"], function(Controller) {
	"use strict";
	return Controller.extend("pruebaIOT.controller.View1", {
		testItemPress: function(evt) {
			// show in a pop-up which list element was pressed
			jQuery.sap.require("sap.m.MessageBox");
			sap.m.MessageBox.show("el mensaje: " + evt.getSource().getBindingContext(), {
				icon: sap.m.MessageBox.Icon.INFORMATION,
				title: "Titulo",
				actions: [sap.m.MessageBox.Action.OK]
			});
		},
		/**
		 *@memberOf pruebaIOT.controller.View1
		 */
		testTap1: function() {
			//This code was generated by the layout editor.
			jQuery.sap.require("sap.m.MessageBox");
			sap.m.MessageBox.show("HUEHUE");
		}
	});
});